INSERT INTO `golfclub`.`course` (`id`, `name`, `sd_img_seq`, `hd_img_seq`, `phone`, `address`, `desc`, `desc1_sd_img_seq`, `desc1_hd_img_seq`, `desc2_sd_img_seq`, `desc2_hd_img_seq`, `traffic_sd_img_seq`, `traffic_hd_img_seq`, `area`, `order`, `state`, `update_date`, `login_username`) VALUES ('1', '北台灣高爾夫球場(1)', '30', '31', '(02)2809-2466', '新北市淡水區八勢路301號', '(1)座落於大屯山西麓，由已故的中日兩位高壇耆老陳金獅與赤星四郎共同規劃設計，海拔150公尺，鄰近淡水河畔且與對岸的觀音山遙遙相望，山、河、海景一攬無遺。這座山地球場，堪稱是台灣地勢起伏最劇烈的球場之一。儘管球道並不長，但難度可不低；隨著山坡地形跌宕蜿蜒的球道，狹窄、高低落差甚大，且狗腿彎與盲眼洞甚多，再加上兩側濃密高聳的樹林與經常出現的OB樁，徹底考驗球員的距離與方向掌控、擊球策略與穩定性。\r「高球同學會」會員擊球優惠：\r平日2150元／假日2550元', '32', '33', '34', '35', '36', '37', '1', '1', '2', '2012-09-25 12:18:06', 'MingIn');
