SELECT * FROM golfclub.video ;

-- result:[totalCount, data:[{title, stopDate, imageSeq, previewUrl, videoUrl, videoSeq},
-- 淘汰版本
SET @NUM=0;
SELECT video.name AS title, 
       video.service_stop_date AS stopDate,
       IF('sd' = 'sd', video.sd_img_seq, video.hd_img_seq) AS imageSeq,
       play_asset_1C AS previewUrl,
       play_asset_1C AS videoUrl,
		@NUM:=@NUM+1 AS videoSeq
  FROM golfclub.video
 WHERE category = '0101';

SELECT count(*) totalCount, category
  FROM golfclub.video,
       (SELECT @state := '2') userState
 WHERE category in (0101,0102)
   AND state between @state and 3
   AND IF(@state = 2, TRUE, now() between service_start_date and service_stop_date)
 Group by category
ORDER BY category;


SELECT videoSeq, title, stopDate, imageSeq, previewUrl, videoUrl, isNew
FROM (
	SELECT CAST(@row_num := IF(@prev_value=video.category,@row_num+1,1) AS UNSIGNED) AS videoSeq,
		   IF(state = '2', concat('[＊]', video.name), video.name) AS title, 
		   video.service_stop_date AS stopDate,
		   IF('sd' = 'sd', video.sd_img_seq, video.hd_img_seq) AS imageSeq,
		   concat(sys.play_uri_1N, video.play_asset_1N) AS previewUrl,
		   concat(sys.play_uri_1N, video.play_asset_1N) AS videoUrl,
          IF(datediff(now(), video.service_start_date) between 0 and 29, 1, 0) AS isNew,
			@prev_value := video.category category
	  FROM golfclub.video video,
          (SELECT * FROM golfclub.system_conf) sys,
		   (SELECT @row_num := 1) x,
		   (SELECT @prev_value := '') y,
		   (SELECT @state := '2') userState
	 WHERE category in (0101)
	   AND state between @state and 3
	   AND IF(@state = 3, now() between service_start_date and service_stop_date, now() < service_stop_date)
  ORDER BY video.category, video.state DESC, video.service_start_date DESC, video.order
	 ) subquery;

 
-- 我是portable ----------------------------------------------------------------------------------------------
SELECT videoSeq, title, stopDate, imageSeq, videoUrl
FROM (
	SELECT CAST(@row_num := IF(@prev_value=video.category,@row_num+1,1) AS UNSIGNED) AS videoSeq,
		   IF(state = '2', concat('[＊]', video.name), video.name) AS title, 
		   video.service_stop_date AS stopDate,
		   video.hd_img_seq AS imageSeq,
		   concat(sys.nScreen_server_N, play_asset_nScreen_N) AS videoUrl,
			@prev_value := video.category category
	  FROM golfclub.video video,
          (SELECT * FROM golfclub.system_conf) sys,
		   (SELECT @row_num := 1) x,
		   (SELECT @prev_value := '') y,
		   (SELECT @state := '2') userState
	 WHERE category in (0102,0103)
	   AND is_nScreen = TRUE
	   AND state between @state and 3
	   AND IF(@state = 3, now() between service_start_date and service_stop_date, now() < service_stop_date)
  ORDER BY video.category, video.state DESC, video.service_start_date DESC, video.order
	 ) subquery;

